import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'

class Deposit extends React.Component {

  constructor(props) {

    super(props);

    this.state = {
          value: "1"
    }

  }

  componentDidMount(){
    axios.get('http://127.0.0.1:5000/coin_deposit?to_address=QX89gmNBV4pT4H3Dds1ZWQxdVtdJuqnfBd')
      .then(res => {
             this.setState({
                        value: res.data
                      });


      })

    /*
    this.timerID = setInterval(
          () =>

            axios.get('http://127.0.0.1:5000/coin_deposit?to_address=QX89gmNBV4pT4H3Dds1ZWQxdVtdJuqnfBd')
                .then(res => {
                       this.setState({
                                  value: res.data
                                });})

          ,
          1000
        )*/
  }

  render() {

    return (

            <div class="background">

                <br/> <br/>

                <h1> Deposit LTC </h1>

                <br/>

                <h3> Send your Litecoin (LTC) to this address: </h3>

                <h3> {this.state.value} </h3>

                <br/>

                <div class="bold">Important: Please use the address above only once.</div>

                <br/> <br/>

                <Link to="/">Back</Link>

             </div>

            );
        }
    }

export default Deposit;
