import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'

class Deposit extends React.Component {

  constructor(props) {

    super(props);

    this.state = {
          value: "1"
    }

      axios.get('http://127.0.0.1:5000/get_an_address')
          .then(res => {
            this.state = {value: res.data};
            this.state.value = res.data;

          })

  }

  componentDidMount(){
    axios.get('http://127.0.0.1:5000/get_an_address')
      .then(res => {
        this.state = {value: res.data};
        this.state.value = res.data;

      })

  }

  render() {

    return (

            <div class="background">

                <br/> <br/>

                <h1> Deposit LTC </h1>

                <br/>

                <h3> Account ID: CR840750</h3>

                <h3> Send your Litecoin (LTC) to this address: </h3>

                <br/>

                <h3> {this.state.value} </h3>

                <br/>

                <div class="bold">Important: Please use the address above only once.</div>

                <br/>

                <Link to="/">Back</Link>

             </div>

            );
        }
    }

export default Deposit;
